import { ProgramMetadata } from "@gear-js/api";
import { useState } from "react";
import { useApi, useAlert } from "@gear-js/react-hooks";
import { Box, Button, Card, Center, Heading, VStack, Text } from "@chakra-ui/react";

function ReadState() {
  const { api } = useApi();

  const alert = useAlert();

  const [fullState, setFullState] = useState<any | undefined>(0);

  const color = (fullState[0] && fullState[0][1]) ?? "Black";

  // Add your programID
  const programIDFT =
  "0x258790299f7ba02baf5238dcf203e8c0eae4cac0a7fae02f5d1b31a55b46b686";

  // Add your metadata.txt
  const meta =
  "";



  const metadata = ProgramMetadata.from(meta);

  const getState = () => {
    api.programState
      .read({ programId: programIDFT, payload: "" }, metadata)
      .then((result) => {
        setFullState(result.toJSON());
      })
      .catch(({ message }: Error) => alert.error(message));
  };

  getState();

  return (
    
      <Center>
        <VStack  >
          
          <Heading  color='black' >State Contract</Heading>
          <p   >{JSON.stringify(fullState)}</p>
          
          

          
        </VStack>
      </Center>
    
  );
}

export { ReadState };
